SAMPLE_MESSAGE = ''

def test_channel_message(client):
    # channel_message endpoint test
    assert client.post('/channel_message', data={'hex_message': SAMPLE_MESSAGE}).status_code == 200

# Black-box testing
def black_box(message, response):
    assert player.ingest_message(message) == response

def test_black_box_prefund():
    message = ''
    response = ''
    black_box(message, response)

def test_black_box_postfund():
    message = ''
    response = ''
    black_box(message, response)

def test_black_box_game_from_propose():
    message = ''
    response = ''
    black_box(message, response)

def test_black_box_game_from_reveal():
    message = '-'
    response = ''
    black_box(message, response)
